const servicesItemArray=document.querySelectorAll(".services-item"),servicesDescItemArray=document.querySelectorAll(".services-desc-item");for(let i=0;i<servicesItemArray.length;i++)servicesItemArray[i].addEventListener("click",()=>{servicesItemArray.forEach(e=>{e.classList.remove("current")}),servicesDescItemArray.forEach(e=>{e.classList.remove("choice-current")}),servicesItemArray[i].classList.add("current"),servicesDescItemArray[i].classList.add("choice-current")});
